    /// What-if analysis: what happens if node is closed?
    /// closed_set is array of booleans indicating already-closed nodes.
    pub fn what_if_close(&self, node: usize, closed_set: &[u8]) -> JsValue {
        let closed: Vec<bool> = closed_set.iter().map(|&b| b != 0).collect();
        let result = whatif::what_if_close(self, node, &closed);
        JsValue::from_serde(&result).unwrap_or(JsValue::NULL)
    }

    /// Top N issues by cascade impact
    pub fn top_what_if(&self, closed_set: &[u8], n: usize) -> JsValue {
