class TimeTravel {
    constructor(history, graph) {
        this.history = history;
        this.graph = graph;
        this.currentIdx = 0;
    }

    goToCommit(idx) {
        const commit = this.history.commits[idx];

        // Add new nodes with 'appear' animation
        commit.beads_added.forEach(id => {
            this.graph.graphData().nodes.push(
                createNodeWithAnimation(id, 'appear')
            );
        });

        // Close nodes with 'shrink' animation
        commit.beads_closed.forEach(id => {
            animateNodeClose(id);
        });

        this.graph.graphData(this.graph.graphData());
    }

    play(speed = 1) {
        this.playing = true;
        const step = () => {
            if (!this.playing) return;
            this.currentIdx++;
            if (this.currentIdx >= this.history.commits.length) {
                this.playing = false;
                return;
            }
            this.goToCommit(this.currentIdx);
            setTimeout(step, 1000 / speed);
        };
        step();
    }
}
```

